import { map } from "lodash";
import React, { useContext } from "react";
import styled from "styled-components";

import { AppContext } from "../appState";

const SidebarContainer = styled.div`
  background: #f1f1f1;
  padding: 10px 15px;
  border-radius: 8px;
  width: 200px;
  display: flex;
  flex-direction: column;
  height: fit-content;
`;

const OptionsContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 10px;

  pre {
    cursor: pointer;
    font-size: 14px;
    color: darkgray;
    margin: 0;
    padding: 0;

    &:hover {
      color: steelblue;
    }
  }
`;

const SidebarHeader = styled.h4`
  margin: 0;
  padding: 0;
  color: steelblue;
`;

const SidebarTop = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  border-bottom: 1px solid darkgray;
  color: steelblue;
  margin: 10px 0;
  padding-bottom: 5px;
`;

const AddButton = styled.button`
  background: transparent;
  border: none;
  margin: 0;
  padding: 0;
  cursor: pointer;
`;

const CharacterLink = styled.a`
  color: darkgray;

  &:focus,
  &:active {
    color: blue;
  }

  &:hover {
    color: steelblue;
  }
`;

const Sidebar: React.FC = () => {
  const { state, dispatch } = useContext(AppContext);

  const addCategory = () => {
    const newCategory: string = "hey";
    dispatch({ type: "addCharacter", payload: newCategory });
  };

  return (
    <SidebarContainer>
      <SidebarTop>
        <SidebarHeader>Categories</SidebarHeader>
        <AddButton>+</AddButton>
      </SidebarTop>
      <OptionsContainer>
        {state.categories.map((category) => (
          <pre>
            <span>{category}</span>
          </pre>
        ))}
      </OptionsContainer>
      <SidebarHeader>Characters</SidebarHeader>
      <OptionsContainer>
        {map(state.characters, (character) => (
          <pre>
            <CharacterLink href={`#${character.name}`}>
              {character.displayName}
            </CharacterLink>
          </pre>
        ))}
      </OptionsContainer>
    </SidebarContainer>
  );
};

export default Sidebar;
