import React, { createContext, Dispatch } from "react";

import categories from "./output/categories.json";
import characters from "./output/characters.json";
import conditions from "./output/conditions.json";
import dialogue from "./output/dialogue.json";
import responses from "./output/responses.json";

export interface AppState {
  categories: string[];
  characters: Character[];
  conditions: Condition[];
  dialogue: Dialogue[];
  responses: DialogueResponse[];
}

export type Payload =
  | string
  | Character
  | Condition
  | Dialogue
  | DialogueResponse;

export interface AppAction {
  type: string;
  payload: Payload;
}

export const defaultState: AppState = {
  categories,
  characters,
  conditions,
  dialogue,
  responses,
};

export const appReducer = (state: AppState, dispatch: AppAction) => {
  switch (type) {
    case "addCategory":
      return { ...state, categories: [...state.categories, payload as string] };
    case "removeCategory":
      return {
        ...state,
        categories: [...state.categories].filter((item) => item !== payload),
      };
    case "addCharacter":
      return {
        ...state,
        characters: [...state.categories, payload as Character],
      };
    case "removeCharacter":
      return {
        ...state,
        characters: [...state.characters].filter(
          (item: Character) => item.name !== payload
        ),
      };
    case "addCondition":
      return {
        ...state,
        conditions: [...state.conditions, payload as Condition],
      };
    case "removeCondition":
      return {
        ...state,
        conditions: [...state.conditions].filter(
          (item: Condition) => item.name !== payload
        ),
      };
    case "addDialogue":
      return {
        ...state,
        dialogue: [...state.dialogue, payload as Dialogue],
      };
    case "removeDialogue":
      return {
        ...state,
        dialogue: [...state.dialogue].filter(
          (item: Dialogue) => item.name !== payload
        ),
      };
    case "addResponse":
      return {
        ...state,
        responses: [...state.responses, payload as DialogueResponse],
      };
    case "removeResponse":
      return {
        ...state,
        responses: [...state.responses].filter(
          (item: DialogueResponse) => item.name !== payload
        ),
      };
    default:
      throw new Error(
        "That ain't right, try using a valid action for the AppState reducer."
      );
  }
};

export const AppContext = createContext<{
  state: AppState;
  dispatch: Dispatch<AppAction>;
}>({ state: defaultState as AppState, dispatch: () => null });
