import { map } from "lodash";
import React, { useContext } from "react";
import styled from "styled-components";

import { AppContext } from "../appState";

const SidebarContainer = styled.div`
  background: #f1f1f1;
  padding: 10px 15px;
  border-radius: 8px;
  width: 200px;
  display: flex;
  flex-direction: column;
`;

const OptionsContainer = styled.div`
  display: flex;
  flex-direction: column;

  pre {
    cursor: pointer;
    font-size: 14px;
    color: darkgray;
    margin: 0;
    padding: 0;

    &:hover {
      color: steelblue;
    }
  }
`;

const SidebarHeader = styled.h4`
  margin: 10px 0;
  border-bottom: 1px solid darkgray;
  padding-bottom: 5px;
  color: steelblue;
`;

const Sidebar: React.FC = () => {
  const { state, dispatch } = useContext(AppContext);

  const saveToJSON = () => {
    dispatch({ type: "saveFiles", payload: "" });
  };

  return (
    <SidebarContainer>
      <button onClick={saveToJSON}>Export to JSON</button>
      <SidebarHeader>Categories</SidebarHeader>
      <OptionsContainer>
        {state.categories.map((category) => (
          <pre>{category}</pre>
        ))}
      </OptionsContainer>
      <SidebarHeader>Characters</SidebarHeader>
      <OptionsContainer>
        {map(state.characters, (character) => (
          <pre>{character.displayName}</pre>
        ))}
      </OptionsContainer>
    </SidebarContainer>
  );
};

export default Sidebar;
