import { map } from "lodash";
import React, { useContext, useState } from "react";
import styled from "styled-components";

import { AppContext } from "../appState";

const SidebarContainer = styled.div`
  background: #f1f1f1;
  padding: 10px 15px;
  border-radius: 8px;
  width: 200px;
  display: flex;
  flex-direction: column;
  height: fit-content;
`;

const OptionsContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 10px;

  pre {
    font-size: 14px;
    color: darkgray;
    margin: 0;
    padding: 0;

    span {
      cursor: hover;
    }

    &:hover {
      color: steelblue;
    }
  }
`;

const SidebarHeader = styled.h4`
  margin: 0;
  padding: 0;
  color: steelblue;
`;

const SidebarTop = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  border-bottom: 1px solid darkgray;
  color: steelblue;
  margin: 10px 0;
  padding-bottom: 5px;
`;

const AddButton = styled.button`
  background: transparent;
  border: none;
  margin: 0;
  padding: 0;
  cursor: pointer;
`;

const CharacterLink = styled.a`
  color: darkgray;

  &:focus,
  &:active {
    color: blue;
  }

  &:hover {
    color: steelblue;
  }
`;

const Sidebar: React.FC = () => {
  const [editing, updateEditing] = useState("");
  const [adding, updateAdding] = useState(false);
  const { state, dispatch } = useContext(AppContext);

  const addCategory = () => {
    updateAdding(true);
  };

  const addCharacter = () => {
    const newCharacter = {
      name: "STATUE",
      displayName: "Interactive Statue",
      default: "DLG_STATUE1",
    };

    const newDialogue = {
      name: "DLG_STATUE1",
      character: "STATUE",
      dialogue: "Hey dude, I'm like totally a statue. Isn't that cool?",
      default: true,
    };
    dispatch({ type: "addCharacter", payload: newCharacter });
    dispatch({ type: "addDialogue", payload: newDialogue });
  };

  const confirmAdd = (event: any) => {
    const newCategory = event.currentTarget.value;
    if (newCategory.length)
      dispatch({ type: "addCategory", payload: newCategory });
    updateAdding(false);
  };

  const handleEnter = (event: any) => {
    if (event.keyCode == 13) confirmAdd(event);
  };

  const updateCategory = (event: any) => {
    if (event.currentTarget.innerText === editing) return;
    dispatch({
      type: "editCategory",
      payload: event.currentTarget.innerText,
      edit: editing,
    });
    updateEditing("");
  };

  return (
    <SidebarContainer>
      <SidebarTop>
        <SidebarHeader>Categories</SidebarHeader>
        <AddButton onClick={addCategory}>+</AddButton>
      </SidebarTop>
      <OptionsContainer>
        {adding && (
          <input
            type="text"
            onBlur={confirmAdd}
            onKeyDown={handleEnter}
            autoFocus
          />
        )}
        {state.categories.map((category) => (
          <pre>
            {editing === category ? (
              <span onBlur={updateCategory} role="textbox" contentEditable>
                {category}
              </span>
            ) : (
              <span onMouseDown={() => updateEditing(category)}>
                {category}
              </span>
            )}
          </pre>
        ))}
      </OptionsContainer>
      <SidebarTop>
        <SidebarHeader>Characters</SidebarHeader>
        <AddButton onClick={addCharacter}>+</AddButton>
      </SidebarTop>
      <OptionsContainer>
        {map(state.characters, (character) => (
          <pre>
            <CharacterLink href={`#${character.name}`}>
              {character.displayName}
            </CharacterLink>
          </pre>
        ))}
      </OptionsContainer>
    </SidebarContainer>
  );
};

export default Sidebar;
