import React, { useContext } from "react";
import styled from "styled-components";

import { AppContext } from "../appState";

const CharacterContainer = styled.div`
  background: #f1f1f1;
  padding: 10px 15px;
  border-radius: 8px;
  display: flex;
  flex-direction: column;
`;

const CharacterName = styled.h4`
  margin: 5px 0 0 0;
`;

const DialogueHeader = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

const ResponsesWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

interface CharacterProps {
  character: Character;
}

const CharacterBuilder: React.FC<CharacterProps> = ({ character }) => {
  const { state } = useContext(AppContext);

  const getConditional = (cID: string) => {
    return (
      state.conditions.find((condition) => condition.name === cID) || {
        name: "NONE",
        met: true,
      }
    );
  };

  const getResponses = (dID: string) => {
    const responses = state.responses.filter((res) => res.parent === dID);
    return responses.map((resp) => ({
      ...resp,
      conditionMet: getConditional(resp.condition).met,
    }));
  };

  const getDialogue = () => {
    const retrieved = state.dialogue.filter(
      (dialogue) => dialogue.character === character.name
    );
    return retrieved.map((dia) => ({
      ...dia,
      responses: getResponses(dia.name),
    }));
  };

  console.log("HMMM: ", getDialogue());

  return (
    <CharacterContainer>
      <CharacterName>{character.displayName}</CharacterName>
      {getDialogue().map((dialogue) => (
        <div>
          <DialogueHeader>
            <pre>{dialogue.name}</pre>
            <pre>Is Default: {dialogue.default ? "TRUE" : "FALSE"}</pre>
          </DialogueHeader>
          <div>"{dialogue.dialogue}"</div>
          <ResponsesWrapper>
            {dialogue.responses.map((resp) => (
              <div>
                <div>Name: {resp.name}</div>
                <div>
                  "[{resp.category}] {resp.dialogue}""
                </div>
                <div></div>
                <div>Next: {resp.next}</div>
                <div>
                  {`Condition: ${resp.condition} (Met: ${resp.conditionMet})`}
                </div>
              </div>
            ))}
          </ResponsesWrapper>
        </div>
      ))}
    </CharacterContainer>
  );
};

export default CharacterBuilder;
