import { pickBy, reduce } from "lodash";
import React, { useContext, useState } from "react";
import styled from "styled-components";

import { AppContext } from "../appState";

const CharacterContainer = styled.div`
  background: #f1f1f1;
  padding: 10px 15px 20px;
  border-radius: 8px;
  display: flex;
  flex-direction: column;
`;

const CharacterName = styled.h4`
  margin: 5px 0 0 0;
`;

const DialogueHeader = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  border-bottom: 1px solid darkgray;
  margin-bottom: 10px;

  div {
    display: flex;
    flex-direction: row;
  }

  pre {
    color: darkgray;
    border-right: 1px solid darkgray;
    padding: 10px;
    margin: 0;

    &:last-of-type {
      border: none;
    }

    &:first-of-type {
      border-right: 1px solid darkgray;
    }
  }
`;

const DialogueWrapper = styled.div`
  display: flex;
  flex-direction: column;
  border: 1px solid darkgray;
  margin-top: 20px;
`;

const ResponsesWrapper = styled.div`
  display: flex;
  flex-direction: column;
  padding: 5px 20px 0;
`;

const ResponseContainer = styled.div`
  margin-top: 10px;
  border: 1px solid darkgray;

  &:last-of-type {
    margin-bottom: 20px;
  }
`;

const True = styled.span`
  color: green;
`;

const False = styled.span`
  color: red;
`;

const DialogueParagraph = styled.p`
  padding: 10px;
  margin: 0;
`;

const DialogueID = styled.pre`
  cursor: text;
`;

const EditButton = styled.div`
  cursor: pointer;
  display: flex;
  align-items: center;
  padding: 0 10px;
  text-transform: uppercase;
  font-size: 10px;
  letter-spacing: 1px;

  &:hover {
    color: #ffffff;
    background: steelblue;
  }
`;

interface ResponseRender extends DialogueResponse {
  conditionMet: boolean;
}

interface DialogueRender extends Dialogue {
  responses: ResponseRender[];
}

interface CharacterProps {
  character: Character;
}

const CharacterBuilder: React.FC<CharacterProps> = ({ character }) => {
  const { state, dispatch } = useContext(AppContext);
  const [editing, updateEditing] = useState("");

  const getConditional = (cID: string) =>
    state.conditions[cID] || {
      name: "NONE",
      met: true,
    };

  const getResponses = (dID: string): DialogueResponse[] =>
    Object.values(pickBy(state.responses, (res) => res.parent === dID));

  const getDialogue = () =>
    reduce(
      state.dialogue,
      (results, dialogue, key) => {
        if (dialogue.character === character.name) {
          results = [
            ...results,
            {
              ...dialogue,
              responses: getResponses(dialogue.name).map((resp) => ({
                ...resp,
                conditionMet: getConditional(resp.condition).met,
              })),
            },
          ];
        }
        return results;
      },
      [] as unknown as DialogueRender[]
    );

  const editSection = (section: any) => {
    const sec = { ...section, name: "DLG_STATUE420" };
    dispatch({ type: "editDialogue", payload: sec, edit: "DLG_STATUE2" });
    updateEditing(section.name);
  };

  const saveDialogue = (ID: string, payload: Dialogue) => {
    updateEditing("");
  };

  const addDialogue = () => {};

  const addResponse = () => {};

  return (
    <CharacterContainer>
      <CharacterName>{character.displayName}</CharacterName>
      {getDialogue().map((dialogue) => (
        <DialogueWrapper id={dialogue.name}>
          {editing === dialogue.name ? (
            <DialogueBuilder dialogue={dialogue} />
          ) : (
            <>
              <DialogueHeader>
                <DialogueID>{dialogue.name}</DialogueID>
                <div>
                  <pre>{dialogue.default && <True>DEFAULT</True>}</pre>
                  <EditButton onClick={() => editSection(dialogue.name)}>
                    Edit
                  </EditButton>
                </div>
              </DialogueHeader>
              <DialogueParagraph>"{dialogue.dialogue}"</DialogueParagraph>
            </>
          )}
          <ResponsesWrapper>
            {dialogue.responses.map((resp) => (
              <ResponseContainer id={resp.name}>
                {editing === resp.name ? (
                  <ResponseBuilder response={resp} />
                ) : (
                  <>
                    <DialogueHeader>
                      <DialogueID>{resp.name}</DialogueID>
                      <pre>
                        <a href={`#${resp.next}`}>Next: {resp.next}</a>
                      </pre>
                      <div>
                        <pre>
                          Condition:{" "}
                          {resp.conditionMet ? (
                            <True>{resp.condition}</True>
                          ) : (
                            <False>{resp.condition}</False>
                          )}
                        </pre>
                        <EditButton onClick={() => editSection(resp)}>
                          Edit
                        </EditButton>
                      </div>
                    </DialogueHeader>
                    <DialogueParagraph>
                      "[{resp.category}] {resp.dialogue}"
                    </DialogueParagraph>
                  </>
                )}
              </ResponseContainer>
            ))}
          </ResponsesWrapper>
        </DialogueWrapper>
      ))}
    </CharacterContainer>
  );
};

interface DialogueBuilderProps {
  dialogue: Dialogue;
}

const DialogueBuilder: React.FC<DialogueBuilderProps> = ({ dialogue }) => {
  console.log("hey");

  return (
    <div>
      <div>hey</div>
    </div>
  );
};

interface ResponseBuilderProps {
  response: DialogueResponse;
}

const ResponseBuilder: React.FC<ResponseBuilderProps> = ({ response }) => {
  console.log("hey");

  return (
    <div>
      <div>hey</div>
    </div>
  );
};

export default CharacterBuilder;
